/**
 * Any CSS included here will be global. The classic template
 * bundles Infima by default. Infima is a CSS framework designed to
 * work well for content-centric websites.
 */

/* You can override the default Infima variables here. */
:root {
  --ifm-color-primary: #2563eb; /* More approachable blue tone */
  --ifm-color-primary-dark: #1d4ed8;
  --ifm-color-primary-darker: #1e40af;
  --ifm-color-primary-darkest: #1e3a8a;
  --ifm-color-primary-light: #3b82f6;
  --ifm-color-primary-lighter: #60a5fa;
  --ifm-color-primary-lightest: #93c5fd;
  --ifm-color-secondary: #64748b; /* Support color */
  --ifm-color-success: #16a34a; /* For positive feedback */
  --ifm-color-info: #0ea5e9; /* For informational elements */
  --ifm-color-warning: #f59e0b; /* For warnings */
  --ifm-color-danger: #dc2626; /* For errors */

  --ifm-font-size-base: 16px; /* Standardize base font size for accessibility */
  --ifm-font-size-small: 87.5%; /* Smaller text */
  --ifm-line-height-base: 1.6; /* Improved readability line height */

  --ifm-code-font-size: 90%;
  --docusaurus-highlighted-code-line-bg: rgba(0, 0, 0, 0.1);

  /* Enhanced accessibility contrast */
  --ifm-color-emphasis-0: #000000;
  --ifm-color-emphasis-100: #111827;
  --ifm-color-emphasis-200: #374151;
  --ifm-color-emphasis-300: #6b7280;
  --ifm-color-emphasis-400: #9ca3af;
  --ifm-color-emphasis-500: #d1d5db;
  --ifm-color-emphasis-600: #e5e7eb;
  --ifm-color-emphasis-700: #f3f4f6;
  --ifm-color-emphasis-800: #f9fafb;
  --ifm-color-emphasis-900: #ffffff;
}

/* For readability concerns, you should choose a lighter palette in dark mode. */
[data-theme='dark'] {
  --ifm-color-primary: #3b82f6;
  --ifm-color-primary-dark: #2563eb;
  --ifm-color-primary-darker: #1d4ed8;
  --ifm-color-primary-darkest: #1e3a8a;
  --ifm-color-primary-light: #60a5fa;
  --ifm-color-primary-lighter: #93c5fd;
  --ifm-color-primary-lightest: #dbeafe;
  --ifm-color-secondary: #94a3b8;
  --ifm-color-success: #34d399;
  --ifm-color-info: #60a5fa;
  --ifm-color-warning: #fbbf24;
  --ifm-color-danger: #f87171;

  --docusaurus-highlighted-code-line-bg: rgba(0, 0, 0, 0.3);

  /* Dark mode contrast adjustments */
  --ifm-color-emphasis-0: #ffffff;
  --ifm-color-emphasis-100: #f1f5f9;
  --ifm-color-emphasis-200: #e2e8f0;
  --ifm-color-emphasis-300: #cbd5e1;
  --ifm-color-emphasis-400: #94a3b8;
  --ifm-color-emphasis-500: #64748b;
  --ifm-color-emphasis-600: #475569;
  --ifm-color-emphasis-700: #334155;
  --ifm-color-emphasis-800: #1e293b;
  --ifm-color-emphasis-900: #0f172a;
}

/* Accessible and approachable styling for content */
.docusaurus-highlight-code-line {
  background-color: rgba(0, 0, 0, 0.1);
  display: block;
  margin: 0 calc(-1 * var(--ifm-pre-padding));
  padding: 0 var(--ifm-pre-padding);
}

/* Enhanced typography for approachability */
.markdown h1,
.markdown h2,
.markdown h3,
.markdown h4,
.markdown h5,
.markdown h6 {
  font-weight: 600;
  margin-top: 1.5rem;
  margin-bottom: 1rem;
}

.markdown h1 {
  font-size: 2rem;
  line-height: 1.25;
}

.markdown h2 {
  font-size: 1.5rem;
  line-height: 1.25;
}

.markdown h3 {
  font-size: 1.25rem;
  line-height: 1.25;
}

/* Accessible link styling */
.markdown a {
  text-decoration: underline;
  text-decoration-thickness: 2px;
  text-underline-offset: 2px;
}

.markdown a:hover {
  background-color: rgba(37, 99, 235, 0.1);
}

/* Improved code block accessibility */
.prism-code {
  border-radius: var(--ifm-code-border-radius);
  font-size: var(--ifm-code-font-size);
  padding: var(--ifm-pre-padding);
}

/* Lesson structure styling for clarity */
.lesson-content {
  max-width: 800px;
  margin: 0 auto;
  padding: 0 1rem;
}

.lesson-content .alert {
  margin: 1.5rem 0;
}

.lesson-content .admonition {
  margin: 1.5rem 0;
}

/* Enhanced focus indicators for accessibility */
button:focus-visible,
a:focus-visible,
input:focus-visible,
select:focus-visible,
textarea:focus-visible {
  outline: 2px solid var(--ifm-color-primary);
  outline-offset: 2px;
}

/* Better spacing for lesson elements */
.lesson-section {
  margin: 2rem 0;
}

.lesson-objectives,
.lesson-summary {
  padding: 1rem;
  border-radius: 6px;
  margin: 1.5rem 0;
}

.lesson-objectives {
  background-color: rgba(59, 130, 246, 0.1);
  border-left: 4px solid var(--ifm-color-primary);
}

.lesson-summary {
  background-color: rgba(163, 163, 163, 0.1);
  border-left: 4px solid var(--ifm-color-secondary);
}

/* Responsive design for all devices */
@media (max-width: 996px) {
  .lesson-content {
    padding: 0 0.5rem;
  }

  .markdown h1 {
    font-size: 1.75rem;
  }

  .markdown h2 {
    font-size: 1.375rem;
  }
}

/* Special styling for hands-on exercises */
.hands-on-exercise {
  background-color: rgba(251, 191, 36, 0.1);
  border-left: 4px solid var(--ifm-color-warning);
  padding: 1rem;
  margin: 1.5rem 0;
  border-radius: 0 4px 4px 0;
}

.hands-on-exercise .admonition-heading {
  color: var(--ifm-color-warning);
}

